import React, { useState, useEffect } from 'react';
import './HeroSection.css';
import { Button } from './Button';
import { getWeb3 } from '../components/pages/Services/connectWallet'
import Web3 from 'web3';
import Web3Modal from "web3modal";

function HeroSection({
  lightBg,
  topLine,
  lightText,
  lightTextDesc,
  headline,
  description,
  buttonLabel,
  img,
  alt,
  imgStart
}) {


  const [account, setaccount] = useState('')
  const [metamaskConnected, setMetamaskConnected] = useState(false)
  const getweb = async () => {
    const web = await getWeb3();
    console.log(web)
  }

  const handleConnectWallet = async () => {
    if (window.ethereum) {
      const data = await window.ethereum.request({ method: "eth_requestAccounts" })
      console.log(data)
      if (data) {
        setaccount(data[0])
        setMetamaskConnected(true)
      }
    }
    else {
      alert("install metamask extension!")
    }
  }

  const handleDisconnectWallet = async () => {
    await getWeb3().eth.currentProvider.disconnect();
    setaccount('')
    setMetamaskConnected(false)
  }
  const checkIsConnected = async () => {
    const acc = await window.ethereum.request({ method: 'eth_accounts' })[0] || false
  }

  useEffect(() => {
    if (checkIsConnected()) {
      setMetamaskConnected(true)
    }
    else { 
      setMetamaskConnected(false)
    }
    getweb();
  }, [])




  return (
    <>
      <div
        className={lightBg ? 'home__hero-section' : 'home__hero-section darkBg'}
      >
        <div className='container'>
          <div
            className='row home__hero-row'
            style={{
              display: 'flex',
              flexDirection: imgStart === 'start' ? 'row-reverse' : 'row'
            }}
          >
            <div className='col'>
              <div className='home__hero-text-wrapper'>
                <div className='top-line'>{topLine}</div>
                <h1 className={lightText ? 'heading' : 'heading dark'}>
                  {headline}
                </h1>
                <p
                  className={
                    lightTextDesc
                      ? 'home__hero-subtitle'
                      : 'home__hero-subtitle dark'
                  }
                >
                  {description}
                </p>

                {!metamaskConnected && <Button buttonSize='btn--wide' buttonColor='blue' onClick={handleConnectWallet}>
                  {buttonLabel}
                </Button>}
                {metamaskConnected && <Button buttonSize='btn--wide' buttonColor='red' disable>
                  wallet connected
                </Button>}

              </div>
            </div>
            <div className='col'>
              <div className='home__hero-img-wrapper'>
                <img src={img} alt={alt} className='home__hero-img' />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HeroSection;
