      <div>
        <input placeholder='amount' name='amount' type='number' onChange={handleAmount} />
      </div>
      <div>
        <input placeholder='message' name='message' type='text' onChange={handleMesssage} />
      </div>
    </div>
  );
};

export default InputDetails;
