import React, { useState, useEffect } from 'react';
import './HeroSection.css';
import { Button } from './Button';
import { getWeb3 } from '../components/pages/Services/connectWallet'

function HeroSection({
  lightBg,
  topLine,
  lightText,
  lightTextDesc,
  headline,
  description,
  buttonLabel,
  img,
  alt,
  imgStart
}) {


  const [account, setaccount] = useState('')
  const getweb = async () => {
    const web = await getWeb3();
    console.log(web)
  }

  const handleConnectWallet = () => {
    if (window.ethereum) {
      window.ethereum.request({ methhod: "eth_requestAccounts" }).then(res => setaccount(res[0]))
    }
    else { }
  }


  useEffect(() => {
    getweb();
  }, [])




  return (
    <>
      <div
        className={lightBg ? 'home__hero-section' : 'home__hero-section darkBg'}
      >
        <div className='container'>
          <div
            className='row home__hero-row'
            style={{
              display: 'flex',
              flexDirection: imgStart === 'start' ? 'row-reverse' : 'row'
            }}
          >
            <div className='col'>
              <div className='home__hero-text-wrapper'>
                <div className='top-line'>{topLine}</div>
                <h1 className={lightText ? 'heading' : 'heading dark'}>
                  {headline}
                </h1>
                <p
                  className={
                    lightTextDesc
                      ? 'home__hero-subtitle'
                      : 'home__hero-subtitle dark'
                  }
                >
                  {description}
                </p>

                <Button buttonSize='btn--wide' buttonColor='blue' onClick={handleConnectWallet}>
                  {buttonLabel}
                </Button>

              </div>
            </div>
            <div className='col'>
              <div className='home__hero-img-wrapper'>
                <img src={img} alt={alt} className='home__hero-img' />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default HeroSection;
